#!/usr/bin/env node

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

console.log('üèåÔ∏è Setting up Madras Mini Golf for local development...\n');

// Check if .env exists
const envPath = path.join(__dirname, '..', '.env');
const envExamplePath = path.join(__dirname, '..', '.env.example');

if (!fs.existsSync(envPath)) {
  console.log('üìù Creating .env file from .env.example...');
  fs.copyFileSync(envExamplePath, envPath);
  console.log('‚úÖ .env file created! Please update it with your database credentials.\n');
} else {
  console.log('‚úÖ .env file already exists.\n');
}

// Check Node.js version
const nodeVersion = process.version;
const majorVersion = parseInt(nodeVersion.split('.')[0].substring(1));

if (majorVersion < 18) {
  console.log('‚ö†Ô∏è  Warning: Node.js 18+ is recommended. Current version:', nodeVersion);
} else {
  console.log('‚úÖ Node.js version:', nodeVersion);
}

// Check if PostgreSQL is available (basic check)
try {
  execSync('psql --version', { stdio: 'ignore' });
  console.log('‚úÖ PostgreSQL CLI available');
} catch (error) {
  console.log('‚ö†Ô∏è  PostgreSQL CLI not found. Make sure PostgreSQL is installed.');
}

console.log('\nüöÄ Setup complete! Next steps:');
console.log('1. Update .env file with your database credentials');
console.log('2. Create PostgreSQL database: createdb madras_mini_golf');
console.log('3. Run migrations: npm run db:migrate');
console.log('4. Start development server: npm run dev');
console.log('\nFor detailed instructions, see README.md');

server/db.ts.content:
import { Pool as NeonPool, neonConfig } from '@neondatabase/serverless';
import { drizzle as drizzleNeon } from 'drizzle-orm/neon-serverless';
import { drizzle as drizzleNode } from 'drizzle-orm/node-postgres';
import { Pool as NodePool } from 'pg';
import ws from "ws";
import * as schema from "@shared/schema";

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

// Detect if we're using Neon or local PostgreSQL
const isNeonDatabase = process.env.DATABASE_URL.includes('neon.tech') || 
                      process.env.DATABASE_URL.includes('neon.database') ||
                      process.env.NODE_ENV === 'production';

let pool: NeonPool | NodePool;
let db: ReturnType<typeof drizzleNeon> | ReturnType<typeof drizzleNode>;

if (isNeonDatabase) {
  // Neon serverless configuration
  neonConfig.webSocketConstructor = ws;
  pool = new NeonPool({ connectionString: process.env.DATABASE_URL });
  db = drizzleNeon({ client: pool, schema });
} else {
  // Local PostgreSQL configuration
  pool = new NodePool({ connectionString: process.env.DATABASE_URL });
  db = drizzleNode(pool, { schema });
}

export { pool, db };

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // serve static built files
  serveStatic(app);

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import bcrypt from "bcryptjs";
import { insertPlayerSchema, insertScoreSchema, insertPricingSchema } from "@shared/schema";
import { z } from "zod";
import session from "express-session";
import ConnectPgSimple from "connect-pg-simple";
import { pool } from "./db";

// Custom game creation schema without totalCost
const createGameSchema = z.object({
  playerId: z.string(),
  playerNames: z.array(z.string()).min(1),
  playerCount: z.number().int().min(1).max(8),
  isWeekend: z.boolean(),
});

// Session user type
declare module "express-session" {
  interface SessionData {
    userId?: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup session middleware
  const PgSession = ConnectPgSimple(session);

  app.use(session({
    store: new PgSession({
      pool: pool,
      tableName: 'session',
    }),
    secret: process.env.SESSION_SECRET || 'your-secret-key-here',
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: false, // Set to true in production with HTTPS
      httpOnly: true,
      maxAge: 30 * 24 * 60 * 60 * 1000, // 30 days
    },
  }));

  // Initialize default admin user and pricing if not exists
  const initializeDefaults = async () => {
    try {
      const adminUser = await storage.getUserByUsername("admin");
      if (!adminUser) {
        const hashedPassword = await bcrypt.hash("admin123", 10);
        await storage.createUser({
          username: "admin",
          password: hashedPassword,
        });
      }

      const currentPricing = await storage.getCurrentPricing();
      if (!currentPricing) {
        const admin = await storage.getUserByUsername("admin");
        if (admin) {
          await storage.createPricing({
            weekdayPrice: "60.00",
            weekendPrice: "80.00",
            updatedBy: admin.id,
          });
        }
      }
    } catch (error) {
      console.error("Error initializing defaults:", error);
    }
  };

  await initializeDefaults();

  // Auth middleware
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Player registration
  app.post("/api/players", async (req, res) => {
    try {
      const playerData = insertPlayerSchema.parse(req.body);
      const player = await storage.createPlayer(playerData);
      res.json(player);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Create game
  app.post("/api/games", async (req, res) => {
    try {
      const gameData = createGameSchema.parse(req.body);

      // Calculate cost based on pricing and day
      const currentPricing = await storage.getCurrentPricing();
      if (!currentPricing) {
        return res.status(400).json({ message: "Pricing not configured" });
      }

      const pricePerPlayer = gameData.isWeekend 
        ? parseFloat(currentPricing.weekendPrice) 
        : parseFloat(currentPricing.weekdayPrice);

      const totalCost = (pricePerPlayer * gameData.playerCount).toFixed(2);

      const game = await storage.createGame({
        ...gameData,
        totalCost,
      });

      res.json(game);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Add scores to game
  app.post("/api/games/:gameId/scores", async (req, res) => {
    try {
      const { gameId } = req.params;
      const scoresData = z.array(insertScoreSchema.omit({ gameId: true })).parse(req.body);

      const scores = await Promise.all(
        scoresData.map(scoreData => 
          storage.createScore({ ...scoreData, gameId })
        )
      );

      res.json(scores);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Get game with scores
  app.get("/api/games/:gameId", async (req, res) => {
    try {
      const { gameId } = req.params;
      const game = await storage.getGame(gameId);

      if (!game) {
        return res.status(404).json({ message: "Game not found" });
      }

      const scores = await storage.getScoresByGame(gameId);
      res.json({ ...game, scores });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get current pricing
  app.get("/api/pricing", async (req, res) => {
    try {
      const pricing = await storage.getCurrentPricing();
      if (!pricing) {
        return res.status(404).json({ message: "Pricing not found" });
      }
      res.json(pricing);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Admin login
  app.post("/api/admin/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Username and password required" });
      }

      const user = await storage.getUserByUsername(username);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValid = await bcrypt.compare(password, user.password);
      if (!isValid) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      res.json({ message: "Login successful", user: { id: user.id, username: user.username } });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Admin logout
  app.post("/api/admin/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Could not log out" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  // Check auth status
  app.get("/api/admin/me", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json({ id: user.id, username: user.username });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Dashboard stats
  app.get("/api/admin/dashboard-stats", requireAuth, async (req, res) => {
    try {
      const today = new Date();
      const yesterday = new Date(today);
      yesterday.setDate(yesterday.getDate() - 1);

      const todayStats = await storage.getDailySales(today);
      const yesterdayStats = await storage.getDailySales(yesterday);

      // Calculate growth percentages
      const gamesGrowth = yesterdayStats.totalGames > 0 
        ? ((todayStats.totalGames - yesterdayStats.totalGames) / yesterdayStats.totalGames * 100).toFixed(1)
        : "0";

      const revenueGrowth = parseFloat(yesterdayStats.totalRevenue) > 0
        ? ((parseFloat(todayStats.totalRevenue) - parseFloat(yesterdayStats.totalRevenue)) / parseFloat(yesterdayStats.totalRevenue) * 100).toFixed(1)
        : "0";

      res.json({
        todayGames: todayStats.totalGames,
        todayRevenue: `‚Çπ${parseFloat(todayStats.totalRevenue).toLocaleString()}`,
        totalPlayers: todayStats.totalPlayers,
        gamesGrowth: `${gamesGrowth}%`,
        revenueGrowth: `${revenueGrowth}%`,
      });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Recent games
  app.get("/api/admin/recent-games", requireAuth, async (req, res) => {
    try {
      const today = new Date();
      const startOfDay = new Date(today);
      startOfDay.setHours(0, 0, 0, 0);
      const endOfDay = new Date(today);
      endOfDay.setHours(23, 59, 59, 999);

      const games = await storage.getGamesByDateRange(startOfDay, endOfDay);

      const recentGames = await Promise.all(games.slice(0, 10).map(async game => {
        const player = await storage.getPlayer(game.playerId);
        return {
          id: game.id,
          playerCount: game.playerCount,
          leadPlayer: player?.name || "Unknown",
          cost: `‚Çπ${parseFloat(game.totalCost).toLocaleString()}`,
          time: new Date(game.completedAt).toLocaleTimeString('en-US', { 
            hour: 'numeric', 
            minute: '2-digit', 
            hour12: true 
          }),
        };
      }));

      res.json(recentGames);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Sales analytics
  app.get("/api/admin/sales/:period", requireAuth, async (req, res) => {
    try {
      const { period } = req.params;
      const today = new Date();

      let stats;
      switch (period) {
        case "day":
          stats = await storage.getDailySales(today);
          break;
        case "week":
          const weekStart = new Date(today);
          weekStart.setDate(today.getDate() - 7);
          stats = await storage.getWeeklySales(weekStart);
          break;
        case "month":
          const monthStart = new Date(today);
          monthStart.setMonth(today.getMonth() - 1);
          stats = await storage.getMonthlySales(monthStart);
          break;
        default:
          return res.status(400).json({ message: "Invalid period" });
      }

      res.json({
        totalGames: stats.totalGames,
        totalRevenue: `‚Çπ${parseFloat(stats.totalRevenue).toLocaleString()}`,
        totalPlayers: stats.totalPlayers,
        avgPerGame: stats.totalGames > 0 
          ? `‚Çπ${(parseFloat(stats.totalRevenue) / stats.totalGames).toFixed(0)}`
          : "‚Çπ0",
      });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Hourly sales breakdown
  app.get("/api/admin/hourly-sales", requireAuth, async (req, res) => {
    try {
      const today = new Date();
      const hourlyData = await storage.getHourlySales(today);

      // Fill in missing hours with zero data (covering all 24 hours)
      const completeHourlyData = [];
      for (let hour = 0; hour <= 23; hour++) {
        const existingData = hourlyData.find(h => h.hour === hour);
        let label;
        if (hour === 0) label = "12AM";
        else if (hour < 12) label = `${hour}AM`;
        else if (hour === 12) label = "12PM";
        else label = `${hour - 12}PM`;

        completeHourlyData.push({
          hour,
          games: existingData?.games || 0,
          revenue: existingData?.revenue || "0",
          label: label,
        });
      }

      res.json(completeHourlyData);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Weekly sales breakdown (7 days)
  app.get("/api/admin/weekly-sales", requireAuth, async (req, res) => {
    try {
      const today = new Date();
      const weeklyData = [];

      for (let i = 6; i >= 0; i--) {
        const date = new Date(today);
        date.setDate(date.getDate() - i);

        const startOfDay = new Date(date);
        startOfDay.setHours(0, 0, 0, 0);
        const endOfDay = new Date(date);
        endOfDay.setHours(23, 59, 59, 999);

        const stats = await storage.getSalesStats(startOfDay, endOfDay);

        weeklyData.push({
          day: i,
          games: stats.totalGames,
          revenue: stats.totalRevenue,
          label: date.toLocaleDateString('en-US', { weekday: 'short' }),
        });
      }

      res.json(weeklyData);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Monthly sales breakdown (30 days)
  app.get("/api/admin/monthly-sales", requireAuth, async (req, res) => {
    try {
      const today = new Date();
      const monthlyData = [];

      // Get last 30 days, group by week
      for (let week = 3; week >= 0; week--) {
        const weekStart = new Date(today);
        weekStart.setDate(weekStart.getDate() - (week * 7 + 6));
        weekStart.setHours(0, 0, 0, 0);

        const weekEnd = new Date(today);
        weekEnd.setDate(weekEnd.getDate() - (week * 7));
        weekEnd.setHours(23, 59, 59, 999);

        const stats = await storage.getSalesStats(weekStart, weekEnd);

        monthlyData.push({
          week: 3 - week,
          games: stats.totalGames,
          revenue: stats.totalRevenue,
          label: `Week ${4 - week}`,
        });
      }

      res.json(monthlyData);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Update pricing
  app.post("/api/admin/pricing", requireAuth, async (req, res) => {
    try {
      const pricingData = insertPricingSchema.parse({
        ...req.body,
        updatedBy: req.session.userId,
      });

      const pricing = await storage.createPricing(pricingData);
      res.json(pricing);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Get pricing history
  app.get("/api/admin/pricing-history", requireAuth, async (req, res) => {
    try {
      const history = await storage.getPricingHistory();
      res.json(history);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get all transactions for a date
  app.get("/api/admin/transactions", requireAuth, async (req, res) => {
    try {
      const { date } = req.query;
      const targetDate = date ? new Date(date as string) : new Date();

      const startOfDay = new Date(targetDate);
      startOfDay.setHours(0, 0, 0, 0);
      const endOfDay = new Date(targetDate);
      endOfDay.setHours(23, 59, 59, 999);

      const games = await storage.getGamesByDateRange(startOfDay, endOfDay);

      const transactions = games.map(game => ({
        id: game.id,
        time: new Date(game.completedAt).toLocaleTimeString('en-US', { 
          hour: 'numeric', 
          minute: '2-digit', 
          hour12: true 
        }),
        player: game.playerNames[0] || "Unknown",
        playerCount: game.playerCount,
        cost: `‚Çπ${parseFloat(game.totalCost).toLocaleString()}`,
        type: game.isWeekend ? "Weekend" : "Weekday",
      }));

      res.json(transactions);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, players, games, scores, pricing,
  type User, type InsertUser,
  type Player, type InsertPlayer,
  type Game, type InsertGame,
  type Score, type InsertScore,
  type Pricing, type InsertPricing
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, gte, lte, and } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Player methods
  createPlayer(player: InsertPlayer): Promise<Player>;
  getPlayer(id: string): Promise<Player | undefined>;

  // Game methods
  createGame(game: InsertGame): Promise<Game>;
  getGame(id: string): Promise<Game | undefined>;
  getGames(startDate?: Date, endDate?: Date): Promise<Game[]>;
  getGamesByDateRange(startDate: Date, endDate: Date): Promise<Game[]>;

  // Score methods
  createScore(score: InsertScore): Promise<Score>;
  getScoresByGame(gameId: string): Promise<Score[]>;

  // Pricing methods
  getCurrentPricing(): Promise<Pricing | undefined>;
  createPricing(pricing: InsertPricing): Promise<Pricing>;
  getPricingHistory(): Promise<Pricing[]>;

  // Analytics methods
  getDailySales(date: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }>;
  getWeeklySales(startDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }>;
  getMonthlySales(startDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }>;
  getHourlySales(date: Date): Promise<Array<{ hour: number; games: number; revenue: string }>>;
  getSalesStats(startDate: Date, endDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async createPlayer(insertPlayer: InsertPlayer): Promise<Player> {
    const [player] = await db
      .insert(players)
      .values(insertPlayer)
      .returning();
    return player;
  }

  async getPlayer(id: string): Promise<Player | undefined> {
    const [player] = await db.select().from(players).where(eq(players.id, id));
    return player || undefined;
  }

  async createGame(insertGame: InsertGame): Promise<Game> {
    const [game] = await db
      .insert(games)
      .values(insertGame)
      .returning();
    return game;
  }

  async getGame(id: string): Promise<Game | undefined> {
    const [game] = await db.select().from(games).where(eq(games.id, id));
    return game || undefined;
  }

  async getGames(startDate?: Date, endDate?: Date): Promise<Game[]> {
    let query = db.select().from(games);

    if (startDate && endDate) {
      query = query.where(and(
        gte(games.completedAt, startDate),
        lte(games.completedAt, endDate)
      ));
    }

    return await query.orderBy(desc(games.completedAt));
  }

  async getGamesByDateRange(startDate: Date, endDate: Date): Promise<Game[]> {
    return await db.select().from(games)
      .where(and(
        gte(games.completedAt, startDate),
        lte(games.completedAt, endDate)
      ))
      .orderBy(desc(games.completedAt));
  }

  async createScore(insertScore: InsertScore): Promise<Score> {
    const [score] = await db
      .insert(scores)
      .values(insertScore)
      .returning();
    return score;
  }

  async getScoresByGame(gameId: string): Promise<Score[]> {
    return await db.select().from(scores)
      .where(eq(scores.gameId, gameId))
      .orderBy(scores.hole, scores.playerName);
  }

  async getCurrentPricing(): Promise<Pricing | undefined> {
    const [currentPricing] = await db.select().from(pricing)
      .orderBy(desc(pricing.updatedAt))
      .limit(1);
    return currentPricing || undefined;
  }

  async createPricing(insertPricing: InsertPricing): Promise<Pricing> {
    const [newPricing] = await db
      .insert(pricing)
      .values(insertPricing)
      .returning();
    return newPricing;
  }

  async getPricingHistory(): Promise<Pricing[]> {
    return await db.select().from(pricing)
      .orderBy(desc(pricing.updatedAt));
  }

  async getDailySales(date: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }> {
    const startOfDay = new Date(date);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(date);
    endOfDay.setHours(23, 59, 59, 999);

    const [result] = await db
      .select({
        totalGames: sql<number>`count(*)::int`,
        totalRevenue: sql<string>`sum(${games.totalCost})::text`,
        totalPlayers: sql<number>`sum(${games.playerCount})::int`,
      })
      .from(games)
      .where(and(
        gte(games.completedAt, startOfDay),
        lte(games.completedAt, endOfDay)
      ));

    return {
      totalGames: result?.totalGames || 0,
      totalRevenue: result?.totalRevenue || "0",
      totalPlayers: result?.totalPlayers || 0,
    };
  }

  async getWeeklySales(startDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }> {
    const endDate = new Date(startDate);
    endDate.setDate(endDate.getDate() + 7);

    const [result] = await db
      .select({
        totalGames: sql<number>`count(*)::int`,
        totalRevenue: sql<string>`sum(${games.totalCost})::text`,
        totalPlayers: sql<number>`sum(${games.playerCount})::int`,
      })
      .from(games)
      .where(and(
        gte(games.completedAt, startDate),
        lte(games.completedAt, endDate)
      ));

    return {
      totalGames: result?.totalGames || 0,
      totalRevenue: result?.totalRevenue || "0",
      totalPlayers: result?.totalPlayers || 0,
    };
  }

  async getMonthlySales(startDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }> {
    const endDate = new Date(startDate);
    endDate.setMonth(endDate.getMonth() + 1);

    const [result] = await db
      .select({
        totalGames: sql<number>`count(*)::int`,
        totalRevenue: sql<string>`sum(${games.totalCost})::text`,
        totalPlayers: sql<number>`sum(${games.playerCount})::int`,
      })
      .from(games)
      .where(and(
        gte(games.completedAt, startDate),
        lte(games.completedAt, endDate)
      ));

    return {
      totalGames: result?.totalGames || 0,
      totalRevenue: result?.totalRevenue || "0",
      totalPlayers: result?.totalPlayers || 0,
    };
  }

  async getHourlySales(date: Date): Promise<Array<{ hour: number; games: number; revenue: string }>> {
    const startOfDay = new Date(date);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(date);
    endOfDay.setHours(23, 59, 59, 999);

    const results = await db
      .select({
        hour: sql<number>`extract(hour from ${games.completedAt})::int`,
        games: sql<number>`count(*)::int`,
        revenue: sql<string>`sum(${games.totalCost})::text`,
      })
      .from(games)
      .where(and(
        gte(games.completedAt, startOfDay),
        lte(games.completedAt, endOfDay)
      ))
      .groupBy(sql`extract(hour from ${games.completedAt})`)
      .orderBy(sql`extract(hour from ${games.completedAt})`);

    return results.map(r => ({
      hour: r.hour,
      games: r.games,
      revenue: r.revenue || "0"
    }));
  }

  async getSalesStats(startDate: Date, endDate: Date): Promise<{ totalGames: number; totalRevenue: string; totalPlayers: number }> {
    const [result] = await db
      .select({
        totalGames: sql<number>`count(*)::int`,
        totalRevenue: sql<string>`sum(${games.totalCost})::text`,
        totalPlayers: sql<number>`sum(${games.playerCount})::int`,
      })
      .from(games)
      .where(and(
        gte(games.completedAt, startDate),
        lte(games.completedAt, endDate)
      ));

    return {
      totalGames: result?.totalGames || 0,
      totalRevenue: result?.totalRevenue || "0",
      totalPlayers: result?.totalPlayers || 0,
    };
  }
}

export const storage = new DatabaseStorage();

